pragma solidity ^0.5.1;

contract hadcoin_ico {
    
    uint public totalRepublicCredit = 1000000;
    uint public USDRepublicCredit = 1000;
    uint public purchase = 0;

    mapping(address => uint) RepublicCreditEquity;
    mapping(address => uint) RepublicCreditUSD;
    
    modifier RepublicCreditBuy(uint USDinvested){
        require(USDinvested  * USDRepublicCredit + purchase <= totalRepublicCredit );
        _;
    }
    
    function EquityinRepublicCredit(address Holder) external view returns (uint){
        return RepublicCreditEquity[Holder];
    }
    
    function equity_in_usd(address Holder) external view returns (uint){
        return RepublicCreditUSD[Holder];
    }
    
    function buy_hadcoins(address Holder,uint USDinvested) external  RepublicCreditBuy(USDinvested){
        uint  hadcoins_bought = USDinvested *USDRepublicCredit;
        RepublicCreditEquity[Holder] += hadcoins_bought;
        RepublicCreditUSD[Holder] = RepublicCreditEquity[Holder] / USDRepublicCredit;
        purchase += hadcoins_bought;
    }
    
    function sell_hadcoins(address Holder,uint hadcoins_sold) external {
        RepublicCreditEquity[Holder] -= hadcoins_sold;
        RepublicCreditUSD[Holder] = RepublicCreditEquity[Holder] / 1000;
        purchase -= hadcoins_sold ;
    }
 
    
}
