pragma solidity ^0.5.1;

contract hadcoin_ico {
    
    uint public totalRepublicCredit = 1000000;
    uint public USDRepublicCredit = 1000;
    uint public purchase = 0;

    mapping(address => uint) RepublicCreditEquity;
    mapping(address => uint) RepublicCreditUSD;
    
    modifier RepublicCreditBuy(uint USDinvested){
        require(USDinvested  * USDRepublicCredit + purchase <= totalRepublicCredit );
        _;
    }
    
    function Equity(address investor) external view returns (uint){
        return RepublicCreditEquity[investor];
    }
    
    function equity_in_usd(address investor) external view returns (uint){
        return RepublicCreditUSD[investor];
    }
    
    function buy_hadcoins(address investor,uint USDinvested) external  RepublicCreditBuy(USDinvested){
        uint  hadcoins_bought = USDinvested *USDRepublicCredit;
        RepublicCreditEquity[investor] += hadcoins_bought;
        RepublicCreditUSD[investor] = RepublicCreditEquity[investor] / USDRepublicCredit;
        purchase += hadcoins_bought;
    }
    
    function sell_hadcoins(address investor,uint hadcoins_sold) external {
        RepublicCreditEquity[investor] -= hadcoins_sold;
        RepublicCreditUSD[investor] = RepublicCreditEquity[investor] / 1000;
        purchase -= hadcoins_sold ;
    }
 
    
}
