pragma solidity ^0.5.1;

contract hadcoin_ico {
    
    uint public totalRepublicCredit = 1000000;
    uint public USDRepublicCredit = 1000;
    uint public purchase = 0;

    mapping(address => uint) RepublicCreditEquity;
    mapping(address => uint) equity_usd;
    
    modifier can_buy_hadcoins(uint usd_invested){
        require(usd_invested  * USDRepublicCredit + purchase <= totalRepublicCredit );
        _;
    }
    
    function equity_in_hadcoins(address investor) external view returns (uint){
        return RepublicCreditEquity[investor];
    }
    
    function equity_in_usd(address investor) external view returns (uint){
        return equity_usd[investor];
    }
    
    function buy_hadcoins(address investor,uint usd_invested) external  can_buy_hadcoins(usd_invested){
        uint  hadcoins_bought = usd_invested *USDRepublicCredit;
        RepublicCreditEquity[investor] += hadcoins_bought;
        equity_usd[investor] = RepublicCreditEquity[investor] / USDRepublicCredit;
        purchase += hadcoins_bought;
    }
    
    function sell_hadcoins(address investor,uint hadcoins_sold) external {
        RepublicCreditEquity[investor] -= hadcoins_sold;
        equity_usd[investor] = RepublicCreditEquity[investor] / 1000;
        purchase -= hadcoins_sold ;
    }
 
    
}
